                when "11" => 
                    -- Toggle
                    Q_internal <= not Q_internal;
                when others =>
                    -- Default case (should not occur)
                    Q_internal <= Q_internal;
            end case;
        end if;
    end process;

    -- Assign internal signal to outputs
    Q <= Q_internal;
    Q_bar <= not Q_internal;

end Behavioral;

